hill = House.create(name: "Hill House", address: Faker::Address.street_address, city: Faker::Address.city, state: "Maine", haunting: "Two roommates and they wanna kiss", image_url: "https://www.fancypantshomes.com/wp-content/uploads/2020/10/HOHH_104_Unit_00564R-Copy.jpg")
haunted_hill = House.create(name: "House on Haunted Hill", address: Faker::Address.street_address, city: Faker::Address.city, state: "Maine", haunting: "Vincent Price going through a messy divorce", image_url: "http://www.midnightonly.com/wp-content/uploads/2011/09/house-on-haunted-hill-1.jpg")
House.create(name: "Whaley House", address: Faker::Address.street_address, city: "San Diego", state: "California", haunting: "Dad problems", image_url: "data:image/jpeg;base64,")

haunted_hill.apartments.create(unit: "Basement", bedrooms: 3, bathrooms: 1, rent: -10000, image: "https://64.media.tumblr.com/7a5de379f0ab5613619ca09a8230537d/tumblr_ncwgsw7KDF1rp0vkjo1_500.gif", hazards: "Pool of acid, ghosts")
Apartment.create(unit: "Second Floor", bedrooms: 6, bathrooms: 6, rent: -10000, image: "http://www.simbasible.com/wp-content/uploads/2018/08/2-8.png", hazards: "Vincent Price, other people", house: haunted_hill)

hill.apartments.create(unit: "Tower", bedrooms: 1, bathrooms: 1, rent: 30, image: "https://static1.srcdn.com/wordpress/wp-content/uploads/2017/11/The-Haunting-of-Hill-House.jpg", hazards: "Falling, not OSHA compliant")
Apartment.create(unit: "2F", bedrooms: 6, bathrooms: 6, rent: 10, image: "https://img.buzzfeed.com/buzzfeed-static/static/2018-10/24/14/asset/buzzfeed-prod-web-03/sub-buzz-19535-1540406899-1.png", hazards: "Ghost knocks on door in the night just don't open up and you'll be fine tbh. Occasionally stained wardrobe.", house_id: hill.id)

puts "Seeded!"
